INSERT INTO blog.setting (`key`, value, description, created_at, updated_at) VALUES ('site-about-content', '<p style="text-align:start;text-indent:2em;">生于南方，2012年毕业一路北上成为一名城续猿，如今已从业了9年了。从2013年开始喜欢跑马拉松，已跑过了6个城市累计1600km里程。目前主要做研发工作，以Golang语言为主，曾经用过很多其他编程语言: PHP,Python,Java等等。</p><p></p><blockquote><em><span style="color:#666666"><span style="font-size:16px"><span style="background-color:#f1f2f3">人生最要紧的就是要保持微笑。生命如此美妙，有太多的事，都值得微笑以对...</span></span></span></em></blockquote>', 'abount内容', null, '2020-03-08 18:07:43');
